INSERT INTO characters (name, role, species, status, bio, full_bio, image, abilities) VALUES
('Рик С-137', 'Гениальный учёный', 'Человек', 'Активен', 'Рик Санчез из измерения C-137 — гениальный учёный и изобретатель, один из самых умных существ в мультивселенной.', '<p>Рик Санчез из измерения C-137 — гениальный учёный и изобретатель, один из самых умных существ в мультивселенной. Дедушка Морти Смита, с которым отправляется в опасные приключения через различные измерения.</p><h3>Характер</h3><p>Циничный, эгоистичный, но глубоко внутри заботящийся о семье. Алкоголик с депрессией и суицидальными наклонностями. Презирает власть и авторитеты.</p><h3>Способности</h3><ul><li>Гениальный интеллект</li><li>Мастер изобретений</li><li>Портальная пушка для путешествий между измерениями</li><li>Экспертные навыки боя и выживания</li></ul>', 'https://cdn.poehali.dev/projects/f9f23ac4-7352-47dd-a4bb-81301617dd90/files/85352bfd-e1db-49a8-b22c-dff89bce449b.jpg', 'Гениальный интеллект, Изобретатель, Междумерные путешествия, Квантовая физика, Бессмертие (через клоны)'),
('Морти С-137', 'Внук Рика, подросток', 'Человек', 'Активен', 'Морти Смит из измерения C-137 — внук Рика, которого постоянно втягивают в опасные приключения.', '<p>Морти Смит из измерения C-137 — внук Рика, которого постоянно втягивают в опасные приключения. Обычный подросток, который вынужден сталкиваться с ужасами мультивселенной.</p><h3>Характер</h3><p>Нервный, неуверенный, но добрый и эмпатичный. Со временем становится более храбрым и решительным. Моральный компас для Рика.</p><h3>Развитие</h3><p>Постепенно приобретает навыки и опыт от приключений с Риком, становясь более компетентным и циничным.</p>', 'https://cdn.poehali.dev/projects/f9f23ac4-7352-47dd-a4bb-81301617dd90/files/7cd37cbe-d901-41ff-8be5-60d44df81654.jpg', 'Быстрая адаптация, Везение, Эмпатия, Скрытый потенциал');